$(document).ready(function(){switch(window.location.pathname){case"/Manage/Create":var t=document.forms[0];t.addEventListener("submit",function(t){t.preventDefault();var e=[];$(".post-field").each(function(t,n){""==$.trim($(n).val())&&e.push(n.id)}),""==tinyMCE.activeEditor.getContent()&&e.push("post-body"),e.length<=0?this.submit():$(e).each(function(t,e){$(document.getElementById(e)).addClass("red-border")})})}});